var Tuenti = require('tuenti');

Tuenti.login('email', 'password', function(request, response) {

	if (response.error) {
	
		console.log('Login incorrect');
		
		return false;
		
	}

	request('getUsersData', { ids: [response.user_id] }, function(response) {

		console.log(response[0].users);

	});

});